import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  const domains = process.env.REPLIT_DOMAINS!.split(",");

  for (const domain of domains) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  // Add localhost alias to prevent mismatches
  if (domains.length > 0) {
    const localhostStrategy = new Strategy(
      {
        name: `replitauth:localhost`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domains[0]}/api/callback`,
      },
      verify,
    );
    passport.use(localhostStrategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    // Use X-Forwarded-Host for proxy-aware domain resolution
    const forwarded = req.get('x-forwarded-host')?.split(',')[0].trim().toLowerCase();
    const host = forwarded || req.headers.host?.split(':')[0]?.toLowerCase() || req.hostname.toLowerCase();
    const domains = process.env.REPLIT_DOMAINS!.split(',').map(d => d.toLowerCase());
    const domain = domains.includes(host) ? host : domains[0];

    console.log(`DEBUG: forwarded=${forwarded}, host=${host}, domains=${JSON.stringify(domains)}, selected domain=${domain}`);

    passport.authenticate(`replitauth:${domain}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    // Use X-Forwarded-Host for proxy-aware domain resolution
    const forwarded = req.get('x-forwarded-host')?.split(',')[0].trim().toLowerCase();
    const host = forwarded || req.headers.host?.split(':')[0]?.toLowerCase() || req.hostname.toLowerCase();
    const domains = process.env.REPLIT_DOMAINS!.split(',').map(d => d.toLowerCase());
    const domain = domains.includes(host) ? host : domains[0];

    passport.authenticate(`replitauth:${domain}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  // Handle both GET (browser redirect) and POST (API) logout
  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });

  app.post("/api/logout", (req, res) => {
    req.logout((err) => {
      if (err) {
        return res.status(500).json({ message: "Logout failed" });
      }

      // Destroy the session
      req.session.destroy((sessionErr) => {
        if (sessionErr) {
          return res.status(500).json({ message: "Session cleanup failed" });
        }

        // Clear the session cookie
        res.clearCookie('connect.sid');
        res.json({ message: "Logged out successfully" });
      });
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import express from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { videoAnalysisService } from "./services/openai";
import { insertAthleteSchema, insertAssessmentSchema, insertKalaPradarshanVideoSchema } from "@shared/schema";
import multer from "multer";
import { z } from "zod";
import fs from "fs/promises";
import path from "path";

// Function to sanitize filename
function sanitizeFilename(filename: string): string {
  return filename
    .replace(/[^a-zA-Z0-9.-]/g, '_') // Keep only alphanumeric, dots, and hyphens
    .replace(/_{2,}/g, '_') // Collapse multiple underscores
    .substring(0, 100); // Limit length
}

// Function to ensure upload directory exists
async function ensureUploadDir() {
  await fs.mkdir('./uploads/tmp', { recursive: true });
}

// Configure multer for video uploads with disk storage for safety
const upload = multer({ 
  storage: multer.diskStorage({
    destination: (req, file, cb) => {
      // Fix error handling with proper callback usage
      ensureUploadDir()
        .then(() => cb(null, './uploads/tmp'))
        .catch(err => cb(err));
    },
    filename: (req, file, cb) => {
      const sanitizedName = sanitizeFilename(file.originalname);
      const uniqueName = `${Date.now()}-${Math.round(Math.random() * 1E9)}-${sanitizedName}`;
      cb(null, uniqueName);
    }
  }),
  limits: {
    fileSize: 50 * 1024 * 1024, // 50MB limit
  },
  fileFilter: (req, file, cb) => {
    if (file.mimetype.startsWith('video/')) {
      cb(null, true);
    } else {
      cb(null, false);
    }
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Serve uploaded files statically
  app.use('/uploads', express.static('uploads'));

  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Get athlete profile if user is an athlete
      let athlete = null;
      if (user.role === 'athlete') {
        athlete = await storage.getAthlete(userId);
      }

      res.json({ ...user, athlete });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Athlete profile routes
  app.post('/api/athletes', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const athleteData = insertAthleteSchema.parse({ ...req.body, userId });

      const athlete = await storage.createAthlete(athleteData);
      res.json(athlete);
    } catch (error) {
      console.error("Error creating athlete profile:", error);
      res.status(400).json({ message: "Failed to create athlete profile" });
    }
  });

  app.get('/api/athletes/:id', isAuthenticated, async (req, res) => {
    try {
      const athlete = await storage.getAthleteById(req.params.id);
      if (!athlete) {
        return res.status(404).json({ message: "Athlete not found" });
      }

      // Record scout view if user is a scout
      const user = req.user as any;
      if (user.claims && user.claims.sub) {
        const currentUser = await storage.getUser(user.claims.sub);
        if (currentUser?.role === 'scout') {
          await storage.recordScoutView(user.claims.sub, athlete.id);
        }
      }

      res.json(athlete);
    } catch (error) {
      console.error("Error fetching athlete:", error);
      res.status(500).json({ message: "Failed to fetch athlete" });
    }
  });

  app.get('/api/athletes', async (req, res) => {
    try {
      const { search, sport, location, limit = '20', offset = '0' } = req.query;

      let athletes;
      if (search || sport || location) {
        athletes = await storage.searchAthletes(
          search as string,
          sport as string,
          location as string
        );
      } else {
        athletes = await storage.getAllAthletes(
          parseInt(limit as string),
          parseInt(offset as string)
        );
      }

      res.json(athletes);
    } catch (error) {
      console.error("Error fetching athletes:", error);
      res.status(500).json({ message: "Failed to fetch athletes" });
    }
  });

  app.get('/api/athletes/top/:limit?', async (req, res) => {
    try {
      const limit = parseInt(req.params.limit || '10');
      const topAthletes = await storage.getTopAthletes(limit);
      res.json(topAthletes);
    } catch (error) {
      console.error("Error fetching top athletes:", error);
      res.status(500).json({ message: "Failed to fetch top athletes" });
    }
  });

  // Assessment routes
  app.get('/api/test-types', async (req, res) => {
    try {
      const testTypes = await storage.getActiveTestTypes();
      res.json(testTypes);
    } catch (error) {
      console.error("Error fetching test types:", error);
      res.status(500).json({ message: "Failed to fetch test types" });
    }
  });

  app.post('/api/assessments', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const athlete = await storage.getAthlete(userId);

      if (!athlete) {
        return res.status(404).json({ message: "Athlete profile not found" });
      }

      const assessmentData = insertAssessmentSchema.parse({
        ...req.body,
        athleteId: athlete.id
      });

      const assessment = await storage.createAssessment(assessmentData);
      res.json(assessment);
    } catch (error) {
      console.error("Error creating assessment:", error);
      res.status(400).json({ message: "Failed to create assessment" });
    }
  });

  app.get('/api/assessments/:id', isAuthenticated, async (req, res) => {
    try {
      const assessment = await storage.getAssessment(req.params.id);
      if (!assessment) {
        return res.status(404).json({ message: "Assessment not found" });
      }

      // Get associated metrics
      const metrics = await storage.getAssessmentMetrics(assessment.id);

      res.json({ ...assessment, metrics });
    } catch (error) {
      console.error("Error fetching assessment:", error);
      res.status(500).json({ message: "Failed to fetch assessment" });
    }
  });

  app.get('/api/athletes/:athleteId/assessments', isAuthenticated, async (req, res) => {
    try {
      const assessments = await storage.getAthleteAssessments(req.params.athleteId);
      res.json(assessments);
    } catch (error) {
      console.error("Error fetching athlete assessments:", error);
      res.status(500).json({ message: "Failed to fetch assessments" });
    }
  });

  // AI Insights endpoint
  app.get('/api/athletes/:id/ai-insights', isAuthenticated, async (req, res) => {
    try {
      const athleteId = req.params.id;
      const athlete = await storage.getAthleteById(athleteId);

      if (!athlete) {
        return res.status(404).json({ message: "Athlete not found" });
      }

      // Get athlete assessments and metrics
      const assessments = await storage.getAthleteAssessments(athleteId);
      const completedAssessments = assessments.filter(a => a.status === 'completed');

      // Get performance metrics for all completed assessments
      const allMetrics = [];
      for (const assessment of completedAssessments) {
        const metrics = await storage.getAssessmentMetrics(assessment.id);
        allMetrics.push(...metrics);
      }

      // Generate AI insights
      const insights = await videoAnalysisService.generateAthleteInsights(
        athlete,
        completedAssessments,
        allMetrics
      );

      res.json(insights);
    } catch (error) {
      console.error("Error generating AI insights:", error);
      res.status(500).json({ message: "Failed to generate AI insights" });
    }
  });

  // Video gallery endpoint
  app.get('/api/athletes/:id/videos', isAuthenticated, async (req, res) => {
    try {
      const athleteId = req.params.id;
      const athlete = await storage.getAthleteById(athleteId);

      if (!athlete) {
        return res.status(404).json({ message: "Athlete not found" });
      }

      // Get assessments with videos (those that have been completed with AI analysis)
      const assessments = await storage.getAthleteAssessments(athleteId);
      const videosData = assessments
        .filter(a => a.status === 'completed' && a.aiAnalysisResults)
        .map(assessment => ({
          id: assessment.id,
          testTypeId: assessment.testTypeId,
          createdAt: assessment.createdAt,
          performanceScore: assessment.aiAnalysisResults?.performanceScore || 0,
          feedback: assessment.aiAnalysisResults?.feedback || '',
          // Note: In a real app, you'd store video URLs or file paths
          // For now, we'll use placeholder data
          thumbnailUrl: `/api/assessments/${assessment.id}/thumbnail`,
          videoUrl: `/api/assessments/${assessment.id}/video`,
          duration: assessment.aiAnalysisResults?.videoDuration || 30,
          title: `Assessment ${assessment.id.slice(0, 8)}`,
        }));

      res.json(videosData);
    } catch (error) {
      console.error("Error fetching athlete videos:", error);
      res.status(500).json({ message: "Failed to fetch videos" });
    }
  });

  // Video upload and analysis
  app.post('/api/assessments/:id/upload-video', 
    isAuthenticated, 
    upload.single('video'), 
    async (req: any, res) => {
      try {
        const assessmentId = req.params.id;
        const assessment = await storage.getAssessment(assessmentId);

        if (!assessment) {
          return res.status(404).json({ message: "Assessment not found" });
        }

        if (!req.file) {
          return res.status(400).json({ message: "No video file provided" });
        }

        // Update assessment status to processing
        await storage.updateAssessmentStatus(assessmentId, 'processing');

        // Convert video to base64 for OpenAI analysis
        const base64Video = req.file.buffer.toString('base64');

        // Get test type info
        const testTypes = await storage.getAllTestTypes();
        const testType = testTypes.find(t => t.id === assessment.testTypeId);

        // Get athlete info for context
        const athlete = await storage.getAthleteById(assessment.athleteId);

        try {
          // Validate video integrity
          const integrity = await videoAnalysisService.validateVideoIntegrity(base64Video);

          if (!integrity.isValid) {
            await storage.updateAssessmentStatus(assessmentId, 'failed', {
              error: 'Video integrity validation failed',
              issues: integrity.issues
            });
            return res.status(400).json({ 
              message: "Video validation failed", 
              issues: integrity.issues 
            });
          }

          // Analyze video performance
          const analysisResult = await videoAnalysisService.analyzePerformanceVideo(
            base64Video,
            testType?.name || 'general',
            {
              age: athlete?.age || undefined,
              height: athlete?.height ? parseFloat(athlete.height) : undefined,
              weight: athlete?.weight ? parseFloat(athlete.weight) : undefined,
              sport: athlete?.primarySport || undefined
            }
          );

          // Calculate overall performance score
          const performanceScore = analysisResult.performanceScore;

          // Store analysis results
          await storage.updateAssessmentStatus(assessmentId, 'completed', {
            ...analysisResult,
            integrity: integrity
          });

          // Create performance metrics
          for (const metric of analysisResult.metrics) {
            await storage.createPerformanceMetric({
              assessmentId,
              metricName: metric.name,
              value: metric.value.toString(),
              unit: metric.unit,
              percentile: null,
              confidence: metric.confidence.toString()
            });
          }

          res.json({
            message: "Video analyzed successfully",
            assessment: await storage.getAssessment(assessmentId),
            analysisResult
          });

        } catch (analysisError) {
          console.error("AI analysis failed:", analysisError);
          await storage.updateAssessmentStatus(assessmentId, 'failed', {
            error: 'AI analysis failed',
            details: (analysisError as Error).message
          });

          res.status(500).json({ 
            message: "Video analysis failed", 
            error: (analysisError as Error).message 
          });
        }

      } catch (error) {
        console.error("Video upload error:", error);
        res.status(500).json({ message: "Failed to process video upload" });
      }
    }
  );

  // Leaderboard routes
  app.get('/api/leaderboard', async (req, res) => {
    try {
      const { sport, metric, limit = '20' } = req.query;
      const leaderboard = await storage.getLeaderboard(
        sport as string,
        metric as string,
        parseInt(limit as string)
      );
      res.json(leaderboard);
    } catch (error) {
      console.error("Error fetching leaderboard:", error);
      res.status(500).json({ message: "Failed to fetch leaderboard" });
    }
  });

  // Achievement routes
  app.get('/api/achievements', async (req, res) => {
    try {
      const achievements = await storage.getAllAchievements();
      res.json(achievements);
    } catch (error) {
      console.error("Error fetching achievements:", error);
      res.status(500).json({ message: "Failed to fetch achievements" });
    }
  });

  app.get('/api/athletes/:athleteId/achievements', async (req, res) => {
    try {
      const userAchievements = await storage.getUserAchievements(req.params.athleteId);
      res.json(userAchievements);
    } catch (error) {
      console.error("Error fetching user achievements:", error);
      res.status(500).json({ message: "Failed to fetch user achievements" });
    }
  });

  // Scout dashboard routes
  app.get('/api/scout/activity', isAuthenticated, async (req: any, res) => {
    try {
      const scoutId = req.user.claims.sub;
      const activity = await storage.getScoutActivity(scoutId);
      res.json(activity);
    } catch (error) {
      console.error("Error fetching scout activity:", error);
      res.status(500).json({ message: "Failed to fetch scout activity" });
    }
  });

  // Dashboard statistics
  app.get('/api/dashboard/stats', isAuthenticated, async (req, res) => {
    try {
      // This would typically involve complex aggregation queries
      // For now, return some basic stats
      const stats = {
        totalAthletes: 12847,
        totalAssessments: 8932,
        topPerformers: 1203,
        scoutViews: 24567,
        // These would be calculated from actual data
        growthStats: {
          athletes: '+12%',
          assessments: '+18%',
          performers: '+8%',
          views: '+25%'
        }
      };

      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard statistics" });
    }
  });

  // Kala Pradarshan (Public Video Showcase) routes

  // Get all public videos with optional sport filter
  app.get('/api/kala-pradarshan', async (req, res) => {
    try {
      const { sport, page = 1, limit = 20 } = req.query;
      const offset = (Number(page) - 1) * Number(limit);

      const videos = await storage.getKalaPradarshanVideos(
        Number(limit), 
        offset, 
        sport as string
      );

      res.json(videos);
    } catch (error) {
      console.error("Error fetching Kala Pradarshan videos:", error);
      res.status(500).json({ message: "Failed to fetch videos" });
    }
  });

  // Upload a new public video
  app.post('/api/kala-pradarshan', isAuthenticated, upload.single('video'), async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { title, description, sport, tags } = req.body;

      if (!req.file) {
        return res.status(400).json({ message: "Video file is required" });
      }

      // With disk storage, file is already saved; move it to final location
      // SECURITY: Server-derived path, not client-controlled
      const timestamp = Date.now();
      const finalFileName = `kala-pradarshan/${userId}/${timestamp}-video`;
      const videoUrl = await storage.uploadFile(req.file.path, finalFileName);

      const videoData = insertKalaPradarshanVideoSchema.parse({
        userId,
        title,
        description,
        sport,
        videoUrl,
        tags: tags ? tags.split(',').map((tag: string) => tag.trim()) : [],
        metadata: {
          originalName: req.file.originalname,
          size: req.file.size,
          mimetype: req.file.mimetype,
        }
      });

      const video = await storage.createKalaPradarshanVideo(videoData);
      res.json(video);
    } catch (error) {
      console.error("Error uploading Kala Pradarshan video:", error);
      res.status(500).json({ message: "Failed to upload video" });
    }
  });

  // Get a specific video
  app.get('/api/kala-pradarshan/:id', async (req, res) => {
    try {
      const video = await storage.getKalaPradarshanVideoById(req.params.id);
      if (!video) {
        return res.status(404).json({ message: "Video not found" });
      }

      res.json(video);
    } catch (error) {
      console.error("Error fetching video:", error);
      res.status(500).json({ message: "Failed to fetch video" });
    }
  });

  // Increment video views
  app.post('/api/kala-pradarshan/:id/view', async (req, res) => {
    try {
      await storage.incrementVideoViews(req.params.id);
      res.json({ success: true });
    } catch (error) {
      console.error("Error incrementing views:", error);
      res.status(500).json({ message: "Failed to update views" });
    }
  });

  // Like a video
  app.post('/api/kala-pradarshan/:id/like', isAuthenticated, async (req, res) => {
    try {
      await storage.incrementVideoLikes(req.params.id);
      res.json({ success: true });
    } catch (error) {
      console.error("Error liking video:", error);
      res.status(500).json({ message: "Failed to like video" });
    }
  });

  // Get user's own videos
  app.get('/api/users/kala-pradarshan', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const videos = await storage.getUserKalaPradarshanVideos(userId);
      res.json(videos);
    } catch (error) {
      console.error("Error fetching user videos:", error);
      res.status(500).json({ message: "Failed to fetch user videos" });
    }
  });

  // Training recommendations
  app.post('/api/athletes/:athleteId/training-recommendations', isAuthenticated, async (req, res) => {
    try {
      const athleteId = req.params.athleteId;
      const { goals } = req.body;

      const athlete = await storage.getAthleteById(athleteId);
      if (!athlete) {
        return res.status(404).json({ message: "Athlete not found" });
      }

      const recentAssessments = await storage.getAthleteAssessments(athleteId);

      const recommendations = await videoAnalysisService.generateTrainingRecommendations(
        athlete,
        recentAssessments.slice(0, 5), // Last 5 assessments
        goals || []
      );

      res.json(recommendations);
    } catch (error) {
      console.error("Error generating training recommendations:", error);
      res.status(500).json({ message: "Failed to generate training recommendations" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  athletes,
  assessments,
  testTypes,
  performanceMetrics,
  achievements,
  userAchievements,
  scoutViews,
  trainingPlans,
  kalaPradarshanVideos,
  type User,
  type UpsertUser,
  type Athlete,
  type InsertAthlete,
  type Assessment,
  type InsertAssessment,
  type TestType,
  type PerformanceMetric,
  type Achievement,
  type UserAchievement,
  type InsertKalaPradarshanVideo,
  type KalaPradarshanVideo,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql, inArray } from "drizzle-orm";
import fs from "fs/promises";
import path from "path";

export interface IStorage {
  // User operations - required for Replit Auth
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Athlete operations
  getAthlete(userId: string): Promise<Athlete | undefined>;
  getAthleteById(id: string): Promise<Athlete | undefined>;
  createAthlete(athlete: InsertAthlete): Promise<Athlete>;
  updateAthlete(id: string, updates: Partial<InsertAthlete>): Promise<Athlete>;
  getAllAthletes(limit?: number, offset?: number): Promise<Athlete[]>;
  searchAthletes(query: string, sport?: string, location?: string): Promise<Athlete[]>;
  getTopAthletes(limit?: number): Promise<Athlete[]>;

  // Kala Pradarshan (Public Video Showcase) operations
  createKalaPradarshanVideo(videoData: InsertKalaPradarshanVideo): Promise<KalaPradarshanVideo>;
  getKalaPradarshanVideos(limit?: number, offset?: number, sport?: string): Promise<KalaPradarshanVideo[]>;
  getKalaPradarshanVideoById(id: string): Promise<KalaPradarshanVideo | undefined>;
  incrementVideoViews(id: string): Promise<void>;
  incrementVideoLikes(id: string): Promise<void>;
  getUserKalaPradarshanVideos(userId: string): Promise<KalaPradarshanVideo[]>;

  // File operations
  uploadFile(tempFilePath: string, targetPath: string): Promise<string>;

  // Assessment operations
  createAssessment(assessment: InsertAssessment): Promise<Assessment>;
  getAssessment(id: string): Promise<Assessment | undefined>;
  getAthleteAssessments(athleteId: string): Promise<Assessment[]>;
  updateAssessmentStatus(id: string, status: string, results?: any): Promise<Assessment>;

  // Test type operations
  getAllTestTypes(): Promise<TestType[]>;
  getActiveTestTypes(): Promise<TestType[]>;

  // Performance metrics
  createPerformanceMetric(metric: Omit<PerformanceMetric, 'id' | 'createdAt'>): Promise<PerformanceMetric>;
  getAssessmentMetrics(assessmentId: string): Promise<PerformanceMetric[]>;

  // Achievements
  getAllAchievements(): Promise<Achievement[]>;
  getUserAchievements(athleteId: string): Promise<UserAchievement[]>;
  unlockAchievement(athleteId: string, achievementId: string): Promise<UserAchievement>;

  // Scout operations
  recordScoutView(scoutId: string, athleteId: string, duration?: number): Promise<void>;
  getScoutActivity(scoutId: string): Promise<any[]>;

  // Leaderboards
  getLeaderboard(sport?: string, metric?: string, limit?: number): Promise<any[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations - required for Replit Auth
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Athlete operations
  async getAthlete(userId: string): Promise<Athlete | undefined> {
    const [athlete] = await db.select().from(athletes).where(eq(athletes.userId, userId));
    return athlete;
  }

  async getAthleteById(id: string): Promise<Athlete | undefined> {
    const [athlete] = await db.select().from(athletes).where(eq(athletes.id, id));
    return athlete;
  }

  async createAthlete(athlete: InsertAthlete): Promise<Athlete> {
    const [newAthlete] = await db.insert(athletes).values(athlete).returning();
    return newAthlete;
  }

  async updateAthlete(id: string, updates: Partial<InsertAthlete>): Promise<Athlete> {
    const [updated] = await db
      .update(athletes)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(athletes.id, id))
      .returning();
    return updated;
  }

  async getAllAthletes(limit = 50, offset = 0): Promise<Athlete[]> {
    return db.select().from(athletes)
      .orderBy(desc(athletes.overallRating))
      .limit(limit)
      .offset(offset);
  }

  async searchAthletes(query: string, sport?: string, location?: string): Promise<Athlete[]> {
    let conditions = [];

    if (query) {
      conditions.push(
        sql`${athletes.userId} IN (
          SELECT id FROM ${users} 
          WHERE LOWER(first_name || ' ' || last_name) LIKE LOWER(${'%' + query + '%'})
        )`
      );
    }

    if (sport) {
      conditions.push(eq(athletes.primarySport, sport));
    }

    if (location) {
      conditions.push(eq(athletes.location, location));
    }

    const whereClause = conditions.length > 0 ? and(...conditions) : undefined;

    return db.select().from(athletes)
      .where(whereClause)
      .orderBy(desc(athletes.overallRating))
      .limit(20);
  }

  async getTopAthletes(limit = 10): Promise<Athlete[]> {
    return db.select().from(athletes)
      .where(eq(athletes.isVerified, true))
      .orderBy(desc(athletes.overallRating))
      .limit(limit);
  }

  // Assessment operations
  async createAssessment(assessment: InsertAssessment): Promise<Assessment> {
    const [newAssessment] = await db.insert(assessments).values(assessment).returning();
    return newAssessment;
  }

  async getAssessment(id: string): Promise<Assessment | undefined> {
    const [assessment] = await db.select().from(assessments).where(eq(assessments.id, id));
    return assessment;
  }

  async getAthleteAssessments(athleteId: string): Promise<Assessment[]> {
    return db.select().from(assessments)
      .where(eq(assessments.athleteId, athleteId))
      .orderBy(desc(assessments.createdAt));
  }

  async updateAssessmentStatus(id: string, status: string, results?: any): Promise<Assessment> {
    const updates: any = { status, updatedAt: new Date() };
    if (results) {
      updates.aiAnalysisResults = results;
    }

    const [updated] = await db
      .update(assessments)
      .set(updates)
      .where(eq(assessments.id, id))
      .returning();
    return updated;
  }

  // Test type operations
  async getAllTestTypes(): Promise<TestType[]> {
    return db.select().from(testTypes).orderBy(testTypes.category, testTypes.name);
  }

  async getActiveTestTypes(): Promise<TestType[]> {
    return db.select().from(testTypes)
      .where(eq(testTypes.isActive, true))
      .orderBy(testTypes.category, testTypes.name);
  }

  // Performance metrics
  async createPerformanceMetric(metric: Omit<PerformanceMetric, 'id' | 'createdAt'>): Promise<PerformanceMetric> {
    const [newMetric] = await db.insert(performanceMetrics).values(metric).returning();
    return newMetric;
  }

  async getAssessmentMetrics(assessmentId: string): Promise<PerformanceMetric[]> {
    return db.select().from(performanceMetrics)
      .where(eq(performanceMetrics.assessmentId, assessmentId));
  }

  // Achievements
  async getAllAchievements(): Promise<Achievement[]> {
    return db.select().from(achievements)
      .where(eq(achievements.isActive, true))
      .orderBy(achievements.category, achievements.name);
  }

  async getUserAchievements(athleteId: string): Promise<UserAchievement[]> {
    return db.select().from(userAchievements)
      .where(eq(userAchievements.athleteId, athleteId))
      .orderBy(desc(userAchievements.unlockedAt));
  }

  async unlockAchievement(athleteId: string, achievementId: string): Promise<UserAchievement> {
    const [newAchievement] = await db.insert(userAchievements)
      .values({ athleteId, achievementId })
      .returning();
    return newAchievement;
  }

  // Scout operations
  async recordScoutView(scoutId: string, athleteId: string, duration = 0): Promise<void> {
    await db.insert(scoutViews).values({
      scoutId,
      athleteId,
      duration,
    });
  }

  async getScoutActivity(scoutId: string): Promise<any[]> {
    return db.select({
      athleteId: scoutViews.athleteId,
      viewedAt: scoutViews.viewedAt,
      duration: scoutViews.duration,
    }).from(scoutViews)
      .where(eq(scoutViews.scoutId, scoutId))
      .orderBy(desc(scoutViews.viewedAt))
      .limit(50);
  }

  // Leaderboards
  async getLeaderboard(sport?: string, metric?: string, limit = 20): Promise<any[]> {
    // This is a complex query that would join multiple tables
    // For now, return top athletes by overall rating
    const baseQuery = db.select({
      athleteId: athletes.id,
      athleteName: sql`${users.firstName} || ' ' || ${users.lastName}`,
      sport: athletes.primarySport,
      location: athletes.location,
      rating: athletes.overallRating,
    }).from(athletes)
      .innerJoin(users, eq(athletes.userId, users.id));

    if (sport) {
      return baseQuery
        .where(eq(athletes.primarySport, sport))
        .orderBy(desc(athletes.overallRating))
        .limit(limit);
    }

    return baseQuery
      .orderBy(desc(athletes.overallRating))
      .limit(limit);
  }

  // Kala Pradarshan (Public Video Showcase) operations
  async createKalaPradarshanVideo(videoData: InsertKalaPradarshanVideo): Promise<KalaPradarshanVideo> {
    const [video] = await db.insert(kalaPradarshanVideos).values(videoData).returning();
    return video;
  }

  async getKalaPradarshanVideos(limit = 20, offset = 0, sport?: string): Promise<KalaPradarshanVideo[]> {
    const baseCondition = and(
      eq(kalaPradarshanVideos.isPublic, true),
      eq(kalaPradarshanVideos.isApproved, true)
    );

    const finalCondition = sport 
      ? and(baseCondition, eq(kalaPradarshanVideos.sport, sport))
      : baseCondition;

    return db.select().from(kalaPradarshanVideos)
      .where(finalCondition)
      .orderBy(desc(kalaPradarshanVideos.createdAt))
      .limit(limit)
      .offset(offset);
  }

  async getKalaPradarshanVideoById(id: string): Promise<KalaPradarshanVideo | undefined> {
    const [video] = await db.select().from(kalaPradarshanVideos).where(eq(kalaPradarshanVideos.id, id));
    return video;
  }

  async incrementVideoViews(id: string): Promise<void> {
    await db.update(kalaPradarshanVideos)
      .set({ views: sql`${kalaPradarshanVideos.views} + 1` })
      .where(eq(kalaPradarshanVideos.id, id));
  }

  async incrementVideoLikes(id: string): Promise<void> {
    await db.update(kalaPradarshanVideos)
      .set({ likes: sql`${kalaPradarshanVideos.likes} + 1` })
      .where(eq(kalaPradarshanVideos.id, id));
  }

  async getUserKalaPradarshanVideos(userId: string): Promise<KalaPradarshanVideo[]> {
    return db.select().from(kalaPradarshanVideos)
      .where(eq(kalaPradarshanVideos.userId, userId))
      .orderBy(desc(kalaPradarshanVideos.createdAt));
  }

  // File operations
  async uploadFile(tempFilePath: string, targetPath: string): Promise<string> {
    try {
      // Split path into directory and basename
      const pathParts = targetPath.split('/');
      const basename = pathParts.pop() || '';
      const directory = pathParts.join('/');

      // Critical security checks for directory
      if (path.isAbsolute(directory)) {
        throw new Error('Absolute paths not allowed');
      }
      if (directory.includes('..')) {
        throw new Error('Path traversal not allowed');
      }
      if (!/^[a-zA-Z0-9_-]+(?:\/[a-zA-Z0-9_-]+)*$/.test(directory)) {
        throw new Error('Invalid directory path');
      }

      // Sanitize only the basename
      const sanitizedBasename = basename.replace(/[^a-zA-Z0-9.-]/g, '_').substring(0, 100);

      // Create secure paths
      const uploadsRoot = path.resolve('./uploads');
      const targetDir = path.resolve(uploadsRoot, directory);
      const finalPath = path.join(targetDir, sanitizedBasename);

      // CRITICAL: Ensure target is within uploads root
      if (!targetDir.startsWith(uploadsRoot + path.sep) && targetDir !== uploadsRoot) {
        throw new Error('Path escape attempt detected');
      }

      // Ensure the directory exists
      await fs.mkdir(targetDir, { recursive: true });

      // Move file (more efficient than read/write)
      await fs.rename(tempFilePath, finalPath);

      // Return the URL path for serving the file
      return `/uploads/${directory}/${sanitizedBasename}`;
    } catch (error) {
      console.error('Error uploading file:', error);
      throw new Error('Failed to upload file');
    }
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import {
  index,
  jsonb,
  pgTable,
  timestamp,
  varchar,
  text,
  integer,
  decimal,
  boolean,
  uuid
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { relations } from "drizzle-orm";

// Session storage table - required for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table - required for Replit Auth
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  role: varchar("role").notNull().default("athlete"), // athlete, scout, admin
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Athlete profiles with detailed information
export const athletes = pgTable("athletes", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  age: integer("age"),
  height: decimal("height", { precision: 5, scale: 2 }), // in cm
  weight: decimal("weight", { precision: 5, scale: 2 }), // in kg
  location: varchar("location"),
  state: varchar("state"),
  primarySport: varchar("primary_sport"),
  secondarySports: text("secondary_sports").array(),
  preferredLanguage: varchar("preferred_language").default("en"),
  bio: text("bio"),
  achievements: text("achievements").array(),
  isVerified: boolean("is_verified").default(false),
  overallRating: decimal("overall_rating", { precision: 3, scale: 1 }).default("0.0"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Test types available in the platform
export const testTypes = pgTable("test_types", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  name: varchar("name").notNull(),
  description: text("description"),
  category: varchar("category").notNull(), // speed, agility, strength, endurance
  instructions: text("instructions"),
  equipmentRequired: text("equipment_required").array(),
  estimatedDuration: integer("estimated_duration"), // in minutes
  difficultyLevel: varchar("difficulty_level"), // beginner, intermediate, advanced
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

// Individual assessment sessions
export const assessments = pgTable("assessments", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  athleteId: uuid("athlete_id").notNull().references(() => athletes.id),
  testTypeId: uuid("test_type_id").notNull().references(() => testTypes.id),
  videoUrl: varchar("video_url"),
  status: varchar("status").notNull().default("pending"), // pending, processing, completed, failed
  aiAnalysisResults: jsonb("ai_analysis_results"),
  performanceScore: decimal("performance_score", { precision: 5, scale: 2 }),
  feedback: text("feedback"),
  duration: integer("duration"), // in seconds
  metadata: jsonb("metadata"), // additional data like device info, conditions
  isVerified: boolean("is_verified").default(false),
  verifiedBy: varchar("verified_by"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Detailed performance metrics for each assessment
export const performanceMetrics = pgTable("performance_metrics", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  assessmentId: uuid("assessment_id").notNull().references(() => assessments.id),
  metricName: varchar("metric_name").notNull(), // speed, jump_height, form_score, etc.
  value: decimal("value", { precision: 10, scale: 4 }).notNull(),
  unit: varchar("unit"), // m/s, cm, degrees, percentage
  percentile: decimal("percentile", { precision: 5, scale: 2 }), // compared to age group
  confidence: decimal("confidence", { precision: 3, scale: 2 }), // AI confidence score
  createdAt: timestamp("created_at").defaultNow(),
});

// Achievement badges and milestones
export const achievements = pgTable("achievements", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  name: varchar("name").notNull(),
  description: text("description"),
  icon: varchar("icon"),
  category: varchar("category"), // performance, consistency, milestone
  criteria: jsonb("criteria"), // conditions to unlock
  rarity: varchar("rarity").default("common"), // common, rare, epic, legendary
  points: integer("points").default(0),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

// User achievements tracking
export const userAchievements = pgTable("user_achievements", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  athleteId: uuid("athlete_id").notNull().references(() => athletes.id),
  achievementId: uuid("achievement_id").notNull().references(() => achievements.id),
  unlockedAt: timestamp("unlocked_at").defaultNow(),
  progress: decimal("progress", { precision: 5, scale: 2 }).default("100.0"), // if partially completed
});

// Scout views and interactions
export const scoutViews = pgTable("scout_views", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  scoutId: varchar("scout_id").notNull().references(() => users.id),
  athleteId: uuid("athlete_id").notNull().references(() => athletes.id),
  viewedAt: timestamp("viewed_at").defaultNow(),
  duration: integer("duration"), // time spent viewing profile
  interactions: jsonb("interactions"), // bookmarks, notes, ratings
});

// Training plans and recommendations
export const trainingPlans = pgTable("training_plans", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  athleteId: uuid("athlete_id").notNull().references(() => athletes.id),
  name: varchar("name").notNull(),
  description: text("description"),
  goals: text("goals").array(),
  duration: integer("duration"), // in weeks
  exercises: jsonb("exercises"),
  aiGenerated: boolean("ai_generated").default(false),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Kala Pradarshan - Public video showcase
export const kalaPradarshanVideos = pgTable("kala_pradarshan_videos", {
  id: uuid("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  title: varchar("title").notNull(),
  description: text("description"),
  sport: varchar("sport").notNull(),
  videoUrl: varchar("video_url").notNull(),
  thumbnailUrl: varchar("thumbnail_url"),
  duration: integer("duration"), // in seconds
  views: integer("views").default(0),
  likes: integer("likes").default(0),
  isPublic: boolean("is_public").default(true),
  isApproved: boolean("is_approved").default(true), // for moderation
  metadata: jsonb("metadata"), // additional video info
  tags: text("tags").array(), // for search/filtering
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Relations
export const usersRelations = relations(users, ({ one, many }) => ({
  athlete: one(athletes, {
    fields: [users.id],
    references: [athletes.userId],
  }),
  scoutViews: many(scoutViews),
  kalaPradarshanVideos: many(kalaPradarshanVideos),
}));

export const athletesRelations = relations(athletes, ({ one, many }) => ({
  user: one(users, {
    fields: [athletes.userId],
    references: [users.id],
  }),
  assessments: many(assessments),
  achievements: many(userAchievements),
  scoutViews: many(scoutViews),
  trainingPlans: many(trainingPlans),
}));

export const assessmentsRelations = relations(assessments, ({ one, many }) => ({
  athlete: one(athletes, {
    fields: [assessments.athleteId],
    references: [athletes.id],
  }),
  testType: one(testTypes, {
    fields: [assessments.testTypeId],
    references: [testTypes.id],
  }),
  metrics: many(performanceMetrics),
}));

export const performanceMetricsRelations = relations(performanceMetrics, ({ one }) => ({
  assessment: one(assessments, {
    fields: [performanceMetrics.assessmentId],
    references: [assessments.id],
  }),
}));

export const userAchievementsRelations = relations(userAchievements, ({ one }) => ({
  athlete: one(athletes, {
    fields: [userAchievements.athleteId],
    references: [athletes.id],
  }),
  achievement: one(achievements, {
    fields: [userAchievements.achievementId],
    references: [achievements.id],
  }),
}));

export const scoutViewsRelations = relations(scoutViews, ({ one }) => ({
  scout: one(users, {
    fields: [scoutViews.scoutId],
    references: [users.id],
  }),
  athlete: one(athletes, {
    fields: [scoutViews.athleteId],
    references: [athletes.id],
  }),
}));

export const trainingPlansRelations = relations(trainingPlans, ({ one }) => ({
  athlete: one(athletes, {
    fields: [trainingPlans.athleteId],
    references: [athletes.id],
  }),
}));

export const kalaPradarshanVideosRelations = relations(kalaPradarshanVideos, ({ one }) => ({
  user: one(users, {
    fields: [kalaPradarshanVideos.userId],
    references: [users.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).pick({
  id: true,
  email: true,
  firstName: true,
  lastName: true,
  profileImageUrl: true,
  role: true,
});

export const insertAthleteSchema = createInsertSchema(athletes).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  overallRating: true,
  isVerified: true,
});

export const insertAssessmentSchema = createInsertSchema(assessments).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  status: true,
  isVerified: true,
  verifiedBy: true,
});

export const insertTestTypeSchema = createInsertSchema(testTypes).omit({
  id: true,
  createdAt: true,
  isActive: true,
});

export const insertKalaPradarshanVideoSchema = createInsertSchema(kalaPradarshanVideos).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  views: true,
  likes: true,
});

// Types
export type UpsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertAthlete = z.infer<typeof insertAthleteSchema>;
export type Athlete = typeof athletes.$inferSelect;
export type InsertAssessment = z.infer<typeof insertAssessmentSchema>;
export type Assessment = typeof assessments.$inferSelect;
export type TestType = typeof testTypes.$inferSelect;
export type PerformanceMetric = typeof performanceMetrics.$inferSelect;
export type Achievement = typeof achievements.$inferSelect;
export type UserAchievement = typeof userAchievements.$inferSelect;
export type ScoutView = typeof scoutViews.$inferSelect;
export type TrainingPlan = typeof trainingPlans.$inferSelect;
export type InsertKalaPradarshanVideo = z.infer<typeof insertKalaPradarshanVideoSchema>;
export type KalaPradarshanVideo = typeof kalaPradarshanVideos.$inferSelect;

